import React, { Component } from "react";
import API from "../utils/API";
import EmployeeRow from "./EmployeeRow";

class Container extends Component {
  state = {
    employees: [],
    filteredEmployees: [],
    filter: "filteredEmployees",
  };
  componentDidMount() {
    API.getUsers().then((res) => {
      console.log(res.data.results);
      this.setState({
        employees: res.data.results,
      });
    });
  }

  sortByName = () => {
    let sorted = [];
    if (this.state.alphabetical) {
      this.setState({ sortedIcon: "▲" });
      sorted = this.state.results.sort((a, b) => {
        let nameA = a.name.first.toLowerCase();
        let nameB = b.name.first.toLowerCase();
        if (nameA < nameB) return -1;
        if (nameA > nameB) return 1;
        return 0;
      });
    } else {
      this.setState({ sortedIcon: "▼" });
      sorted = this.state.results.sort((a, b) => {
        let nameA = a.name.first.toLowerCase();
        let nameB = b.name.first.toLowerCase();
        if (nameA > nameB) return -1;
        if (nameA < nameB) return 1;
        return 0;
      });
    }
    this.setState({
      alphabetical: !this.state.alphabetical,
      alphResults: sorted,
    });
  };

  // function swaps emplooyes to filtered employees
  render() {
    return (
      <>
        {this.state[this.state.filter].map((employee) => {
          return <EmployeeRow emp={employee} />;
        })}{" "}
      </>
    );
  }
}
export default Container;
